import PropTypes from "prop-types"


function Header({ text, bgColor, textColor }) {
    
    const headerStyles = {
        color: textColor,
        backgroundColor: bgColor,
    }
  return (
<header style ={headerStyles}>
       <div className="container">
           <h1>{text}</h1>
       </div>
   </header>
  )
}
Header.defaultProps = {
    text : 'Feedback UI',
    bgColor: 'rgba(0,0,0,0.4)',
    textColor: '#ff6a95',
}
export default Header
